import * as React from 'react';
import * as Linking from 'expo-linking';
import { SafeAreaView, ScrollView, View, Text } from 'react-native';
import { styles } from './styles';

var jsonData = { "parsed_articles": { "breitbart-news": ["https://www.breitbart.com/politics/2021/07/07/joe-biden-claims-mitch-mcconnell-loves-democrats-big-government-programs/", "Biden Claims McConnell 'Loves' Democrats' Big Government 'Programs'", " President Joe Biden responded to a comment in Illinois Wednesday about the \u201ctough fight\u201d ahead regarding the trojan horse infrastructure proposal by saying Sen. Mitch McConnell (R-KY) \u201cloves our programs.\u201d\n\n\u201cMitch McConnell loves our programs,\u201d Biden immediately responded before asking of the people in the room, \u201cDid you see what Mitch McConnell said?\u201d\n\n\u201cHe told me he wasn\u2019t going to get a single vote in order to allow me\u2026 the 1.9 trillion dollar tax cut \u2013 I mean excuse me, program for \u2013 for economic growth,\u201d Biden said, changing the topic from the infrastructure fight to the coronavirus relief package.", "conservative"], "fox-news": ["https://www.foxnews.com/politics/manchin-silent-biden-blm-nominee", "Joe Manchin still silent on Biden nominee with ties to eco-terrorism", " Senator Joe Manchin , D-W.V., continues to remain silent on President Biden\u2019s nominee to lead the Bureau of Land Management (BLM) as the president faces calls to withdraw her nomination over her eco-terrorism ties. Manchin has yet to say a word on whether he supports Tracy Stone-Manning, Biden\u2019s nominee to lead BLM that has come under major scrutiny in recent weeks. The Senate Energy and Natural Resources Committee chairman\u2019s ranking member counterpart on the committee John Barrasso, R-Wyo., has repeatedly called for the president to withdraw Stone-Manning\u2019s nomination. MANCHIN PRESSURED TO OPPOSE BIDEN BLM NOMINEE LINKED TO ECO-TERRORIST PLOT\n\nHowever, Manchin has remained silent in his position on Stone-Manning, with his office not responding to Fox News\u2019 questions on Friday on whether he agrees with Barrasso\u2019s calls to pull Stone-Manning from the nomination or supports Biden\u2019s BLM pick. He even called it a \u2018satisfying justice.\u2019 That\u2019s dangerous and disturbing,\" Senate Energy and Natural Resources Committee ranking member John Barrasso, R-Wyo., told Fox News on Friday.", "conservative"] }, "sentiments": { "breitbart-news": ["negative", 0.524987], "fox-news": ["negative", 0.719865] }, "topic": "Joe Biden" };

export async function queryAPI(query) {
    query = query.replace(' ', '+');
    query = 'https://web-production-3b7b.up.railway.app/api?' + query;
    const res = await fetch(query);
    const resJson = await res.json();
    jsonData = resJson;
}

export default ({ navigation }) => {
    var parsedObj = jsonData["parsed_articles"];
    var sentiments = jsonData["sentiments"];
    var sources = []
    for (var source in parsedObj) {
        sources.push(source);
    }
    return (
        <SafeAreaView style={styles.container}>
            <ScrollView>
                {
                    sources.map((source, index) => {
                        var parsedArr = parsedObj[source]
                        var sentArr = sentiments[source];
                        var url = parsedArr[0];
                        var title = parsedArr[1];
                        var summary = parsedArr[2];
                        var bias = parsedArr[3];
                        var biasStyle;
                        if (bias == "liberal") {
                            biasStyle = styles.subArticleLiberal;
                        } else if (bias == "conservative") {
                            biasStyle = styles.subArticleConservative;
                        } else {
                            biasStyle = styles.subArticleText;
                        }
                        return (
                            <View style = {styles.queryView}>
                                <Text key={index} style={styles.articleText}>
                                    {title}
                                </Text>
                                <View style={{ flexDirection: 'row' }}>
                                    <Text key={source} style={styles.subArticleSource}>
                                        {source}
                                    </Text>
                                    <Text key={bias + " " + source} style={biasStyle}>
                                        {bias}
                                    </Text>
                                </View>
                                <Text key={sentArr[1] + " " + source} style={styles.subArticleText}>
                                    {Math.round(sentArr[1] * 100) + "% " + sentArr[0]}
                                </Text>
                                <Text style = {styles.AIText}>AI Generated Summary:</Text>
                                <Text key={source + " summary"} style={styles.subArticleText}>
                                    {summary}
                                </Text>
                                <Text key={source + " link"} onPress={() => { Linking.openURL(url) }} style={styles.subArticleLink}>
                                    Read the full article
                                </Text>
                            </View>
                        );
                    })
                }
            </ScrollView>
        </SafeAreaView>
    );
}
