import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { Text, ScrollView } from 'react-native';
import SearchBar from "react-native-dynamic-search-bar";
import { queryAPI } from './query';
import { styles } from './styles';

export default ({ navigation }) => {
  const [searchText, setSearchText] = useState("");
  const [loadText, setLoadText] = useState("");
  return (
    <ScrollView contentContainerStyle={styles.scrollContainer}>
      <Text style={styles.header}>Newsflash</Text>
      <SearchBar
        placeholder="Learn about any topic"
        onChangeText={(text) => {setLoadText(""); setSearchText(text)}}
        onClearPress={() => {setLoadText(""); setSearchText("")}}
        onSubmitEditing={() => { setLoadText("Loading..."); queryAPI(searchText).then(() => { setLoadText(""); navigation.navigate('Query Results'); }) }}
      />
      <Text style = {{ color: 'white', marginTop: 20, fontSize: 15 }}>{loadText}</Text>
      <StatusBar style="auto" />
    </ScrollView>
  );
}
